mapper.select:
select distinct t2.id as orderId, t2.order_status as orderStateId, t6.user_name as userName, t6.phone as phone, t2.total_price as orderAllCost, t2.total_count as orderAllGoodsCount, t2.version as version, t5.customer_id as userId from t_store_info t1, t_order_info t2, t_order_details t3, t_goods_info t4, t_sys_customer t5, t_sys_user t6 where t1.manager_id = #{userId} and t1.id = t2.store_id and t2.id = t3.order_id and t3.goods_id = t4.id and t1.invitation_code = t5.invitation_code and t5.customer_id = t6.user_id and t2.user_id = t6.user_id and t2.is_delete = 0 and t3.is_delete = 0
and t2.order_status = #{orderStateId}
and (t4.goods_name like concat('%', #{keyword}, '%') or t1.id like concat('%', #{keyword}, '%'))
