mapper.select:
select t1.id orderId, t1.total_price orderAllCost, t1.order_status orderStateId, t1.store_id storeId, t2.user_name userName, t2.phone phone, t1.defray_time payTime, t1.version version from t_order_info t1, t_sys_user t2 where t1.user_id = t2.user_id and t1.is_delete = 0 and t1.store_id = (select id from t_store_info where manager_id = #{loginUserId} )
and t2.user_name like concat('%', #{userName}, '%')
and t1.id like concat('%', #{orderId}, '%')
and t1.defray_time >= #{payTimeStart}
and t1.defray_time <= #{payTimeEnd}
and t1.defray_time between #{payTimeStart} and #{payTimeEnd}
and t2.phone like concat('%', #{phone}, '%')
and t1.order_status = #{orderStateId}
order by t1.create_time desc

select t1.id as orderId, t1.total_price as orderAllCost, t1.order_status as orderStateId, t1.store_id as storeId, t2.user_name as userName, t2.phone as phone, t1.defray_time as payTime, t1.version as version from t_order_info t1, t_sys_user t2 where t1.user_id = t2.user_id and t1.is_delete = 0
and t2.user_name like concat('%', #{userName}, '%')
and t1.id like concat('%', #{orderId}, '%')
and t1.defray_time >= #{payTimeStart}
and t1.defray_time <= #{payTimeEnd}
and t1.defray_time between #{payTimeStart} and #{payTimeEnd}
and t2.phone like concat('%', #{phone}, '%')
and t1.order_status = #{orderStateId}
order by t1.create_time desc

update.foreach:
update t_order_info
order_status = #{item.orderStateId}, update_user = #{item.updateUser, jdbcType=VARCHAR}, update_time = now(), version = version + 1
id = #{item.orderId, jdbcType=VARCHAR} and version = #{item.version}
